package com.suneee.eas.common.bean.upload;import org.apache.commons.lang.builder.ToStringBuilder;/** * @program: eas-parent * @description: 分块上传类 * @author: liuhai * @create: 2018-09-25 10:32 **/public class ChunkUpload {    private String filename;    private int chunkNumber;    private long chunkSize;    private long currentChunkSize;    private long totalSize;    private int totalChunks;    private String identifier;    private String relativePath;    public String getFilename() {        return filename;    }    public void setFilename(String filename) {        this.filename = filename;    }    public int getChunkNumber() {        return chunkNumber;    }    public void setChunkNumber(int chunkNumber) {        this.chunkNumber = chunkNumber;    }    public long getChunkSize() {        return chunkSize;    }    public void setChunkSize(long chunkSize) {        this.chunkSize = chunkSize;    }    public long getCurrentChunkSize() {        return currentChunkSize;    }    public void setCurrentChunkSize(long currentChunkSize) {        this.currentChunkSize = currentChunkSize;    }    public long getTotalSize() {        return totalSize;    }    public void setTotalSize(long totalSize) {        this.totalSize = totalSize;    }    public int getTotalChunks() {        return totalChunks;    }    public void setTotalChunks(int totalChunks) {        this.totalChunks = totalChunks;    }    public String getIdentifier() {        return identifier;    }    public void setIdentifier(String identifier) {        this.identifier = identifier;    }    public String getRelativePath() {        return relativePath;    }    public void setRelativePath(String relativePath) {        this.relativePath = relativePath;    }    @Override    public String toString() {        return new ToStringBuilder(this)                .append("filename", filename)                .append("chunkNumber", chunkNumber)                .append("chunkSize", chunkSize)                .append("currentChunkSize", currentChunkSize)                .append("totalSize", totalSize)                .append("totalChunks", totalChunks)                .append("identifier", identifier)                .append("relativePath", relativePath)                .toString();    }}