package com.suneee.eas.common.uploader.s3;import com.amazonaws.services.s3.model.CompleteMultipartUploadResult;import com.suneee.eas.common.bean.upload.ChunkUpload;import org.apache.logging.log4j.LogManager;import org.apache.logging.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import java.io.File;import java.io.InputStream;/** * @program: eas-parent * @description: 云存储处理器 * @author: liuhai * @create: 2018-10-22 10:08 **/public class CloudStorageHandler {    private static final Logger log = LogManager.getLogger(CloudStorageHandler.class);    @Autowired    private MultiUploader multiUploader;    /**     * 分块上传（流）     * @param path     * @param inputStream     * @param chunkUpload     * @return     */    public CompleteMultipartUploadResult multiUpload(String path, InputStream inputStream, ChunkUpload chunkUpload) throws Exception{        return this.multiUpload(null, path, inputStream, chunkUpload);    }    /**     * 分块上传操作（流）     * @param bucket     * @param path     * @param inputStream     * @param chunkUpload     * @return     */    public CompleteMultipartUploadResult multiUpload(String bucket, String path, InputStream inputStream, ChunkUpload chunkUpload) throws Exception{        return multiUploader.upload(bucket, path, inputStream, chunkUpload);    }    /**     * 分块上传（file对象）     * @param path     * @param file     * @param chunkUpload     * @return     */    public CompleteMultipartUploadResult multiUpload(String path, File file, ChunkUpload chunkUpload) throws Exception{        return this.multiUpload(null, path, file, chunkUpload);    }    /**     * 分块上传（file对象）     * @param bucket     * @param path     * @param file     * @param chunkUpload     * @return     */    public CompleteMultipartUploadResult multiUpload(String bucket, String path, File file, ChunkUpload chunkUpload) throws Exception{        return multiUploader.upload(bucket, path, file, chunkUpload);    }}