mapper.select:
SELECT 	ts.ID,ts.TASKID,ts.ACTINSTID,ts.TRANSTYPE,ts.ACTION,ts.CREATEUSERID,ts.TRANSRESULT,
        ts.ASSIGNEE,ts.CREATETIME,run.processName processName,run.runId runId,run.subject subject,
        type.typeId typeId,run.globalFlowNo 
FROM BPM_PRO_TRANSTO ts join BPM_PRO_RUN run  on ts.ACTINSTID = run.actinstid 
left join SYS_GL_TYPE type on run.typeid = type.typeId







	 AND ts.CREATEUSERID=#{createUserId}
	 AND upper(run.subject) LIKE upper(#{subject}) escape '|'  
	 AND ts.CREATETIME  >=#{begincreatetime,jdbcType=TIMESTAMP} 
	 AND ts.CREATETIME  <=#{endcreatetime,jdbcType=TIMESTAMP} 
	 AND type.nodePath like #{nodePath} 
	 AND upper(run.processName) LIKE upper(#{processName}) escape '|' 
	 AND run.globalFlowNo  LIKE #{globalFlowNo} 
	 AND run.typeId = #{typeId} 

		AND run.typeId IN

			#{typeId}


	 AND run.defId = #{defId} 

		AND run.defId IN

			#{defId}


	 AND (run.globalFlowNo  LIKE #{searchCondition} OR upper(run.processName) LIKE upper(#{searchCondition}) escape '|')



order by ${orderField} ${orderSeq}


order by ts.CREATETIME  desc

SELECT DISTINCT run.defid as defId,count(DISTINCT run.runid) total
FROM BPM_PRO_TRANSTO ts join BPM_PRO_RUN run  on ts.ACTINSTID = run.actinstid

	 AND ts.CREATEUSERID=#{createUserId}
	 AND upper(run.subject) LIKE upper(#{subject}) escape '|'  
	 AND ts.CREATETIME  >=#{begincreatetime,jdbcType=TIMESTAMP} 
	 AND ts.CREATETIME  <=#{endcreatetime,jdbcType=TIMESTAMP} 
	 AND upper(run.processName) LIKE upper(#{processName}) escape '|' 
	 AND run.globalFlowNo  LIKE #{globalFlowNo} 
	 AND run.typeId = #{typeId} 

		AND run.typeId IN

			#{typeId}



GROUP BY run.defid
