package com.suneee.eas.oa.controller.car;import com.suneee.eas.common.component.Pager;import com.suneee.eas.common.component.QueryFilter;import com.suneee.eas.common.component.ResponseMessage;import com.suneee.eas.common.constant.FunctionConstant;import com.suneee.eas.common.constant.ModuleConstant;import com.suneee.eas.common.utils.ContextSupportUtil;import com.suneee.eas.common.utils.DateUtil;import com.suneee.eas.common.utils.RequestUtil;import com.suneee.eas.common.utils.StringUtil;import com.suneee.eas.oa.model.car.CarTrendsHistory;import com.suneee.eas.oa.service.car.CarTrendsHistoryService;import org.apache.logging.log4j.LogManager;import org.apache.logging.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import javax.servlet.http.HttpServletRequest;import java.util.List;/** * @program: eas-parent * @description: * @author: liuhai * @create: 2018-10-11 10:11 **/@Controller@RequestMapping(ModuleConstant.CAR_MODULE+ FunctionConstant.CAR_TRENDS_HISTORY)public class CarTrendsHistoryApiController {    private static final Logger logger = LogManager.getLogger(CarTrendsHistoryApiController.class);    @Autowired    private CarTrendsHistoryService carTrendsHistoryService;    @RequestMapping("listPage")    @ResponseBody    public ResponseMessage listPage(HttpServletRequest request){        try {            String startTime = RequestUtil.getString(request, "startTime");            String endTime = RequestUtil.getString(request, "endTime");            QueryFilter queryFilter = new QueryFilter("list", request);            if(StringUtil.isNotEmpty(startTime)){                queryFilter.addFilter("startTime", DateUtil.getDate(startTime));            }            if(StringUtil.isNotEmpty(endTime)){                queryFilter.addFilter("endTime" , DateUtil.getDate(endTime));            }            String enterpriseCode = ContextSupportUtil.getCurrentEnterpriseCode();            queryFilter.addFilter("enterpriseCode", enterpriseCode);            queryFilter.addFilter("assigneeId", ContextSupportUtil.getCurrentUserId());            Pager<CarTrendsHistory> list = carTrendsHistoryService.getPageBySqlKey(queryFilter);            return ResponseMessage.success("获取车辆动态历史分页列表成功。", list);        } catch (Exception e) {            logger.error("获取车辆动态历史分页列表失败:" + e.getMessage(), e);            return ResponseMessage.fail("获取车辆动态历史分页列表失败！"+e.getMessage(), e);        }    }    @RequestMapping("list")    @ResponseBody    public ResponseMessage list(HttpServletRequest request){        try {            String startTime = RequestUtil.getString(request, "startTime");            String endTime = RequestUtil.getString(request, "endTime");            QueryFilter queryFilter = new QueryFilter("list", request);            if(StringUtil.isNotEmpty(startTime)){                queryFilter.addFilter("startTime", DateUtil.getDate(startTime));            }            if(StringUtil.isNotEmpty(endTime)){                queryFilter.addFilter("endTime" , DateUtil.getDate(endTime));            }            String enterpriseCode = ContextSupportUtil.getCurrentEnterpriseCode();            queryFilter.addFilter("enterpriseCode", enterpriseCode);            List<CarTrendsHistory> list = carTrendsHistoryService.getListBySqlKey(queryFilter);            return ResponseMessage.success("获取车辆动态历史列表成功。", list);        } catch (Exception e) {            logger.error("获取车辆动态历史列表失败:" + e.getMessage(), e);            return ResponseMessage.fail("获取车辆动态历史列表失败！"+e.getMessage(), e);        }    }    /**     * 获取车辆安排详情     * @param request     * @return     */    @RequestMapping("findById")    @ResponseBody    public ResponseMessage findById(HttpServletRequest request){        Long id = RequestUtil.getLong(request, "id");        if(id == 0){            logger.error("获取车辆动态历史失败，参数不能为空！");            return ResponseMessage.fail("获取车辆动态历史失败，参数不能为空！");        }        try {            CarTrendsHistory carTrendsHistory = carTrendsHistoryService.findById(id);            return ResponseMessage.success("查询详情成功。", carTrendsHistory);        } catch (Exception e) {            logger.error("查询详情失败："+e.getMessage(), e);            return ResponseMessage.fail("查询详情失败！"+ e.getMessage(), e);        }    }}