package com.suneee.eas.oa.dao.car;import com.suneee.eas.common.dao.BaseDao;import com.suneee.eas.oa.model.car.ArrangeCarDriver;import org.springframework.stereotype.Repository;/** * @program: eas-parent * @description: 生成车辆安排、车辆信息、驾驶员关联Dao * @author: liuhai * @create: 2018-08-24 14:05 **/@Repositorypublic class ArrangeCarDriverDao extends BaseDao<ArrangeCarDriver> {    /**     * 根据车辆安排id删除 关系表     * @param arrId     */    public void deleteByArrId(Long arrId){        getSqlSessionTemplate().delete(getNamespace() + ".deleteByArrId", arrId);    }    /**     * 根据派车id获取 关系表     * @param arrId     * @return     */    public ArrangeCarDriver findByArrId(Long arrId){        return getSqlSessionTemplate().selectOne(getNamespace() + ".findByArrId", arrId);    }}