package com.suneee.eas.oa.dao.car;import com.suneee.eas.common.dao.BaseDao;import com.suneee.eas.oa.model.car.CarArrange;import org.springframework.stereotype.Repository;import java.util.HashMap;import java.util.Map;/** * @program: eas-parent * @description: 车俩安排dao * @author: liuhai * @create: 2018-08-21 16:54 **/@Repositorypublic class CarArrangeDao extends BaseDao<CarArrange>{    /**     * 更新taskId     * @param applyId     * @param taskId     */    public void updateTaskId(Long applyId, String taskId) {        Map<String,Object> params=new HashMap<String,Object>();        params.put("applyId",applyId);        params.put("taskId",taskId);        getSqlSessionTemplate().update(getNamespace()+".updateTaskId",params);    }    public CarArrange findByApplyId(Long applyId){        return getSqlSessionTemplate().selectOne(getNamespace()+".findByApplyId", applyId);    }    /**     * 根据车辆申请id删除     * @param applyId     */    public void deleteByApplyId(Long applyId){        getSqlSessionTemplate().delete(getNamespace()+".deleteByApplyId", applyId);    }}