package com.suneee.eas.oa.dao.car;import com.suneee.eas.common.dao.BaseDao;import com.suneee.eas.oa.model.car.CarInfo;import org.springframework.stereotype.Repository;import java.util.HashMap;import java.util.Map;/** * @program: eas-parent * @description: 车辆信息 * @author: liuhai * @create: 2018-08-20 13:08 **/@Repositorypublic class CarInfoDao extends BaseDao<CarInfo>{    /**     * 车牌号是否存在     * @param plateNum     * @return     */    public boolean isPlateNumExist(String plateNum, Long carId){        Map<String, Object> params = new HashMap<>();        params.put("plateNum", plateNum);        params.put("carId", carId);        long num = (Long)getSqlSessionTemplate().selectOne(getNamespace()+".isPlateNumExist", params);        return num>0;    }    /**     * 车辆信息逻辑删除     * @param carId     */    public void removeById(Long carId){        getSqlSessionTemplate().update(getNamespace() +".removeById", carId);    }}