package com.suneee.eas.oa.dao.system;import com.suneee.eas.common.dao.BaseDao;import com.suneee.eas.oa.model.system.RoleResources;import org.springframework.stereotype.Repository;import java.util.List;/** * @program: eas-parent * @description: 角色资源dao * @author: liuhai * @create: 2018-08-23 10:46 **/@Repositorypublic class RoleResourcesDao extends BaseDao<RoleResources> {    /**     * 获取角色资源     * @param roleId     * @return     */    public List<RoleResources> findListByRoleId(Long roleId){        return getSqlSessionTemplate().selectList(getNamespace() + ".findListByRoleId", roleId);    }    /**     * 根据角色id删除角色资源关系     * @param roleId     */    public void deleteByRoleId(Long roleId){        getSqlSessionTemplate().delete(getNamespace() + ".deleteByRoleId", roleId);    }}