package com.suneee.eas.oa.dao.user;import com.suneee.eas.common.dao.BaseDao;import com.suneee.eas.common.utils.ContextSupportUtil;import com.suneee.eas.common.utils.StringUtil;import com.suneee.platform.model.system.SysOrgRole;import org.springframework.stereotype.Repository;import java.util.HashMap;import java.util.List;import java.util.Map;/** * @program: eas-parent * @description: 组织角色类 * @author: liuhai * @create: 2018-08-15 10:10 **/@Repositorypublic class SysOrgRoleDao extends BaseDao<SysOrgRole> {    /**     * 获取组织角色     * @param orgId     * @param enterpriseCode     * @return     */    public List<SysOrgRole> getRolesByOrgId(Long orgId,String enterpriseCode) {        Map<String, Object> params = new HashMap<String, Object>();        params.put("orgId", orgId);        if (StringUtil.isEmpty(enterpriseCode)){            enterpriseCode=ContextSupportUtil.getCurrentEnterpriseCode();        }        params.put("enterpriseCode", enterpriseCode);        return getSqlSessionTemplate().selectList(getNamespace() + ".getRolesByOrgId", params);    }    public List<SysOrgRole> getRolesByOrgId(Long orgId) {        return getRolesByOrgId(orgId,null);    }}