package com.suneee.eas.oa.dao.user;import com.suneee.eas.common.dao.BaseDao;import com.suneee.platform.model.system.UserRole;import org.springframework.stereotype.Repository;import java.util.HashMap;import java.util.Map;/** * @program: eas-parent * @description: 用户角色dao * @author: liuhai * @create: 2018-08-27 11:44 **/@Repositorypublic class UserRoleDao extends BaseDao<UserRole> {    /**     * 根据用户id和角色id获取 用户角色关系     * @param userId     * @param roleId     * @return     */    public UserRole getByUserIdAndRoleId(Long userId, Long roleId){        Map<String, Object> params = new HashMap<>();        params.put("userId", userId);        params.put("roleId", roleId);        return getSqlSessionTemplate().selectOne(getNamespace() + ".getByUserIdAndRoleId", params);    }}