package com.suneee.eas.oa.model.car;import com.fasterxml.jackson.databind.annotation.JsonDeserialize;import com.fasterxml.jackson.databind.annotation.JsonSerialize;import com.suneee.eas.common.component.jackson.LongJsonDeserializer;import com.suneee.eas.common.component.jackson.LongJsonSerializer;import com.suneee.eas.common.model.BaseModel;import org.apache.commons.lang3.builder.ToStringBuilder;/** * @program: eas-parent * @description: 车辆安排、车辆信息、驾驶员关联类 * @author: liuhai * @create: 2018-08-24 13:41 **/public class ArrangeCarDriver extends BaseModel {    /**     * 关联类主键id     */    @JsonSerialize(using = LongJsonSerializer.class)    @JsonDeserialize(using = LongJsonDeserializer.class)    private Long id;    /**     * 派车id     */    @JsonSerialize(using = LongJsonSerializer.class)    @JsonDeserialize(using = LongJsonDeserializer.class)    private Long arrId;    /**     * 车辆id     */    @JsonSerialize(using = LongJsonSerializer.class)    @JsonDeserialize(using = LongJsonDeserializer.class)    private Long carId;    /**     * 车辆名称     */    private String carName;    /**     * 驾驶员id     */    @JsonSerialize(using = LongJsonSerializer.class)    @JsonDeserialize(using = LongJsonDeserializer.class)    private Long driverId;    /**     * 驾驶员姓名     */    private String driverName;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public Long getArrId() {        return arrId;    }    public void setArrId(Long arrId) {        this.arrId = arrId;    }    public Long getCarId() {        return carId;    }    public void setCarId(Long carId) {        this.carId = carId;    }    public String getCarName() {        return carName;    }    public void setCarName(String carName) {        this.carName = carName;    }    public Long getDriverId() {        return driverId;    }    public void setDriverId(Long driverId) {        this.driverId = driverId;    }    public String getDriverName() {        return driverName;    }    public void setDriverName(String driverName) {        this.driverName = driverName;    }    @Override    public String toString() {        return new ToStringBuilder(this)                .append("id", id)                .append("arrId", arrId)                .append("carId", carId)                .append("carName", carName)                .append("driverId", driverId)                .append("driverName", driverName)                .append("createBy", createBy)                .append("createByName", createByName)                .append("createTime", createTime)                .append("updateBy", updateBy)                .append("updateByName", updateByName)                .append("updateTime", updateTime)                .toString();    }}