package com.suneee.eas.oa.model.car;import com.fasterxml.jackson.databind.annotation.JsonDeserialize;import com.fasterxml.jackson.databind.annotation.JsonSerialize;import com.suneee.eas.common.component.jackson.LongJsonDeserializer;import com.suneee.eas.common.component.jackson.LongJsonSerializer;import com.suneee.eas.common.model.BaseModel;import org.apache.commons.lang3.builder.ToStringBuilder;import java.util.Date;/** * @program: eas-parent * @description: 车辆信息 * @author: liuhai * @create: 2018-08-20 09:38 **/public class CarInfo extends BaseModel{    /**     * 商务车     */    public static final int CAR_TYPE_BUSINESS = 0;    /**     * 大型车     */    public static final int CAR_TYPE_OVERSIZE = 1;    /**     * 小型车     */    public static final int CAR_TYPE_LANDAULET = 2;    /**     * 其他车型     */    public static final int CAR_TYPE_OTHER = 3;    /**     * 正常     */    public static final int CAR_STATUS_WELL = 0;    /**     * 维修中     */    public static final int CAR_STATUS_REPAIR = 1;    /**     * 扣押中     */    public static final int CAR_STATUS_DETAINED = 2;    /**     * 报废     */    public static final int CAR_STATUS_USELESS = 3;    /**     * 未锁定     */    public static final boolean CAR_ISLOCK_FALSE = false;    /**     * 锁定     */    public static final boolean CAR_ISLOCK_TRUE = true;    /**     *车辆id     */    @JsonSerialize(using = LongJsonSerializer.class)    @JsonDeserialize(using = LongJsonDeserializer.class)    private Long carId;    /**     *车牌号     */    private String plateNum;    /**     *车辆名称     */    private String name;    /**     *车辆类型：0：商务车，1：大型车，2：小型车，3：其他     */    private int type;    /**     *排量     */    private String displacement;    /**     *车辆颜色     */    private String color;    /**     *座位数     */    private Short seatCount;    /**     *车架号     */    private String identNum;    /**     *燃油标号     */    private String fuelLabel;    /**     *油卡id     */    @JsonSerialize(using = LongJsonSerializer.class)    @JsonDeserialize(using = LongJsonDeserializer.class)    private Long cardId;    /**     *综合油耗     */    private String oilConsumption;    /**     *车辆照片     */    private String picture;    /**     *管理者id     */    @JsonSerialize(using = LongJsonSerializer.class)    @JsonDeserialize(using = LongJsonDeserializer.class)    private Long managerId;    /**     *管理者姓名     */    private String managerName;    /**     *购买价格     */    private Double purchasePrice;    /**     *购买日期     */    private Date purchaseDate;    /**     *车辆状态：0：正常，1：维修中，2：扣押中，3：报废     */    private int status;    /**     *备注     */    private String remarks;    /**     *所属企业     */    private String enterpriseCode;    /**     *是否锁定：0=未锁定，1=已锁定     */    private Boolean isLock = false;    /**     *是否删除：0=未删除，1=已删除     */    private Boolean isDelete = false;    /**     * 当前里程数     */    private String currentMile;    /**     * 油卡编号     */    private String cardNum;    public Long getCarId() {        return carId;    }    public void setCarId(Long carId) {        this.carId = carId;    }    public String getPlateNum() {        return plateNum;    }    public void setPlateNum(String plateNum) {        this.plateNum = plateNum;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getType() {        return type;    }    public void setType(int type) {        this.type = type;    }    public String getDisplacement() {        return displacement;    }    public void setDisplacement(String displacement) {        this.displacement = displacement;    }    public String getColor() {        return color;    }    public void setColor(String color) {        this.color = color;    }    public Short getSeatCount() {        return seatCount;    }    public void setSeatCount(Short seatCount) {        this.seatCount = seatCount;    }    public String getIdentNum() {        return identNum;    }    public void setIdentNum(String identNum) {        this.identNum = identNum;    }    public String getFuelLabel() {        return fuelLabel;    }    public void setFuelLabel(String fuelLabel) {        this.fuelLabel = fuelLabel;    }    public Long getCardId() {        return cardId;    }    public void setCardId(Long cardId) {        this.cardId = cardId;    }    public String getOilConsumption() {        return oilConsumption;    }    public void setOilConsumption(String oilConsumption) {        this.oilConsumption = oilConsumption;    }    public String getPicture() {        return picture;    }    public void setPicture(String picture) {        this.picture = picture;    }    public Long getManagerId() {        return managerId;    }    public void setManagerId(Long managerId) {        this.managerId = managerId;    }    public String getManagerName() {        return managerName;    }    public void setManagerName(String managerName) {        this.managerName = managerName;    }    public Double getPurchasePrice() {        return purchasePrice;    }    public void setPurchasePrice(Double purchasePrice) {        this.purchasePrice = purchasePrice;    }    public Date getPurchaseDate() {        return purchaseDate;    }    public void setPurchaseDate(Date purchaseDate) {        this.purchaseDate = purchaseDate;    }    public int getStatus() {        return status;    }    public void setStatus(int status) {        this.status = status;    }    public String getRemarks() {        return remarks;    }    public void setRemarks(String remarks) {        this.remarks = remarks;    }    public String getEnterpriseCode() {        return enterpriseCode;    }    public void setEnterpriseCode(String enterpriseCode) {        this.enterpriseCode = enterpriseCode;    }    public Boolean getIsLock() {        return isLock;    }    public void setIsLock(Boolean lock) {        this.isLock = lock;    }    public Boolean getIsDelete() {        return isDelete;    }    public void setIsDelete(Boolean delete) {        isDelete = delete;    }    public String getCurrentMile() {        return currentMile;    }    public void setCurrentMile(String currentMile) {        this.currentMile = currentMile;    }    public String getCardNum() {        return cardNum;    }    public void setCardNum(String cardNum) {        this.cardNum = cardNum;    }    @Override    public String toString() {        return new ToStringBuilder(this)                .append("carId", carId)                .append("plateNum", plateNum)                .append("name", name)                .append("type", type)                .append("displacement", displacement)                .append("color", color)                .append("seatCount", seatCount)                .append("identNum", identNum)                .append("fuelLabel", fuelLabel)                .append("cardId", cardId)                .append("oilConsumption", oilConsumption)                .append("picture", picture)                .append("managerId", managerId)                .append("managerName", managerName)                .append("purchasePrice", purchasePrice)                .append("purchaseDate", purchaseDate)                .append("status", status)                .append("remarks", remarks)                .append("enterpriseCode", enterpriseCode)                .append("isLock", isLock)                .append("isDelete", isDelete)                .append("createBy", createBy)                .append("createByName", createByName)                .append("createTime", createTime)                .append("updateBy", updateBy)                .append("updateByName", updateByName)                .append("updateTime", updateTime)                .append("currentMile", currentMile)                .append("cardNum", cardNum)                .toString();    }}