package com.suneee.eas.oa.service.car.impl;import com.suneee.eas.common.service.impl.BaseServiceImpl;import com.suneee.eas.common.utils.ContextSupportUtil;import com.suneee.eas.oa.dao.car.CarArrangeHistoryDao;import com.suneee.eas.oa.model.car.CarArrangeHistory;import com.suneee.eas.oa.service.car.CarArrangeHistoryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Date;/** * @program: eas-parent * @description: * @author: liuhai * @create: 2018-10-11 09:31 **/@Servicepublic class CarArrangeHistoryServiceImpl extends BaseServiceImpl<CarArrangeHistory> implements CarArrangeHistoryService{    private CarArrangeHistoryDao carArrangeHistoryDao;    @Autowired    public void setCarArrangeHistoryDao(CarArrangeHistoryDao carArrangeHistoryDao) {        this.carArrangeHistoryDao = carArrangeHistoryDao;        setBaseDao(carArrangeHistoryDao);    }    @Override    public int save(CarArrangeHistory model){        model.setCreateBy(ContextSupportUtil.getCurrentUserId());        model.setCreateTime(new Date());        return super.save(model);    }}