package com.suneee.eas.oa.service.car.impl;import com.suneee.eas.common.service.impl.BaseServiceImpl;import com.suneee.eas.common.utils.ContextSupportUtil;import com.suneee.eas.oa.dao.car.CarTrendsHistoryDao;import com.suneee.eas.oa.model.car.CarTrendsHistory;import com.suneee.eas.oa.service.car.CarTrendsHistoryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Date;/** * @program: eas-parent * @description: * @author: liuhai * @create: 2018-10-11 13:39 **/@Servicepublic class CarTrendsHistoryServiceImpl extends BaseServiceImpl<CarTrendsHistory> implements CarTrendsHistoryService{    private CarTrendsHistoryDao carTrendsHistoryDao;    @Autowired    public void setCarTrendsHistoryDao(CarTrendsHistoryDao carTrendsHistoryDao) {        this.carTrendsHistoryDao = carTrendsHistoryDao;        setBaseDao(carTrendsHistoryDao);    }    @Override    public int save(CarTrendsHistory model){        model.setCreateBy(ContextSupportUtil.getCurrentUserId());        model.setCreateTime(new Date());        return super.save(model);    }}