package com.suneee.eas.oa.service.user.impl;import com.suneee.eas.common.service.impl.BaseServiceImpl;import com.suneee.eas.oa.dao.user.SysOrgRoleDao;import com.suneee.eas.oa.service.user.SysOrgRoleService;import com.suneee.platform.model.system.SysOrgRole;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @program: eas-parent * @description: 组织角色类 * @author: liuhai * @create: 2018-08-15 10:09 **/@Servicepublic class SysOrgRoleServiceImpl extends BaseServiceImpl<SysOrgRole> implements SysOrgRoleService {    private SysOrgRoleDao sysOrgRoleDao;    @Autowired    public void setSysOrgRoleDao(SysOrgRoleDao sysOrgRoleDao) {        this.sysOrgRoleDao = sysOrgRoleDao;        setBaseDao(sysOrgRoleDao);    }    /**     * 根据orgId 获取组织权限     * @param orgId     * @return     */    public List<SysOrgRole> getRolesByOrgId(Long orgId){        return sysOrgRoleDao.getRolesByOrgId(orgId);    }    @Override    public List<SysOrgRole> getRolesByOrgId(Long orgId, String enterpriseCode) {        return sysOrgRoleDao.getRolesByOrgId(orgId,enterpriseCode);    }}