package com.suneee.eas.oa.util;import com.suneee.eas.oa.model.car.CarApply;/** * @program: eas-parent * @description: 车辆管理工具类 * @author: liuhai * @create: 2018-10-11 11:56 **/public class CarManagerUtil {    public static String getStatusString(int status){        String result = "";        switch (status){            case CarApply.STATUS_AUDIT_AUDITING:result="审批中";break;            case CarApply.STATUS_AUDIT_FAIL:result="审批拒绝";break;            case CarApply.STATUS_CAR_ARRANGE_PENDING:result="待派车";break;            case CarApply.STATUS_CAR_ARRANGE_FAIL:result="派车拒绝";break;            case CarApply.STATUS_CAR_DRIVE_PENDDING:result="已派车";break;            case CarApply.STATUS_CAR_DRIVE_FAIL:result="不出车";break;            case CarApply.STATUS_CAR_BACK_PENDING:result="已出车";break;            case CarApply.STATUS_CAR_BACK_DONE:result="已还车";break;            case CarApply.STATUS_AUDIT_FEEBACK:result="已撤回";break;            case CarApply.STATUS_CANCEL:result="已取消";break;            default:result="已派车";        }        return result;    }}